import React, { useState } from 'react';
import MEAL from './meal.js';
import './order.css';

const Order = () => {
  const [cartItems, setCartItems] = useState([]);
  const addToCart = (meal) => {
    setCartItems([...cartItems, meal]);
  };
  const removeFromCart = (meal) => {
    const updatedCart = cartItems.filter(item => item.id !== meal.id);
    setCartItems(updatedCart);
  };

  return (
    <div>
      <h1>Welcome to Yang's Dumpling</h1>
      <div className="menu-container">
        {MEAL.meal.map(meal => (
          <div key={meal.id} className="menu-item">
            <img src={meal.picture} alt={meal.title} />
            <h2>{meal.title}</h2>
            <p>{meal.introduction}</p>
            <p>Price: {meal.price}</p>
            <button onClick={() => addToCart(meal)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <Cart cartItems={cartItems} removeFromCart={removeFromCart} />
    </div>
  );
};

const Cart = ({ cartItems, removeFromCart }) => {
  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cartItems.map(item => (
        <div key={item.id} className="cart-item">
          <img src={item.picture} alt={item.title} />
          <h3>{item.title}</h3>
          <p>Price: {item.price}</p>
          <button onClick={() => removeFromCart(item)}>Remove</button>
        </div>
      ))}
      <p>Total Price: ${cartItems.reduce((total, item) => total + parseFloat(item.price), 0).toFixed(2)}</p>
      <button>Checkout</button>
    </div>
  );
};

export default Order;
